package operators;

public class Bitwise_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int x =10;
	        int y = 21;
	       
	        if(x++ > 10 || ++y < 21 ){
	        System.out.print("Inside if  ");
	        }else{
	        System.out.print("Inside else  ");
	        }
	        System.out.println(x +" "+y);
	     }
	}   
